"use strict";function getSelectedFilter(t){for(const e of t)if(e.classList.contains("landing-filter__button_active"))return e}const filter=document.querySelector(".landing-filter__games"),filtersButtons=filter.children;let selectedFilter=getSelectedFilter(filtersButtons);filter.addEventListener("click",t=>{t=t.target.dataset.filter;if(t!==selectedFilter.dataset.filter){for(const e of filtersButtons)e.classList.remove("landing-filter__button_active");document.querySelector(`[data-filter=${t}]`).classList.add("landing-filter__button_active"),selectedFilter=getSelectedFilter(filtersButtons)}});